#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, vec2 U)
float ln (vec2 p, vec2 a, vec2 b) {
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float pie (vec2 p, vec2 a, vec2 b) {
	 if (length(a-b)<1e-3||length(a)<1.||length(b)<1.) return 1e9;
    vec2 m = 0.5*(a+b); // midpoint
    return abs(dot(p-m,b-m)/dot(b-m,b-m)); // pojection
}
bool cmp (vec2 p, vec2 a, vec2 b, vec2 c, vec2 d) {
    float l1 = ln(p,a,b),
          l2 = ln(p,c,d);
    if (l1<.5&&l2<.5) return length(a-b)<length(b-c);
    else return l1<l2;

}

// Sort Particles 🕷
Main
{
    Q = A(U);
    for (float x=-2.;x<=2.;x++)
    for (float y=-2.;y<=2.;y++)
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        if (length(U-q.xy)<length(U-Q.xy))
            Q = q;
    }
}

//Sort Graph 🕸
Main
{
    Q = B(U);
    Q.xy = A(U).xy;
    for (float x=-2.;x<=2.;x++)
    for (float y=-2.;y<=2.;y++)
    {
        vec2 u = vec2(x,y);
        vec4
            aa= A(U+u),
            b = B(U+u),
            a = vec4(A(b.xy).xy,A(b.zw).xy),
            q = vec4(A(Q.xy).xy,A(Q.zw).xy);
        if (cmp(U,q.xy,aa.xy,q.xy,q.zw))
        {
            Q.zw = q.zw = aa.xy;
        } else
        if (cmp(U,a.xy,a.zw,q.xy,q.zw))
        {
            q = a;
            Q = b;
        } else
        if (cmp(U,q.xy,a.xy,q.xy,q.zw))
        {
            q.zw = a.xy;
            Q.zw = a.zw;
        }
    }
    Q.xy = A(Q.xy).xy;
    Q.zw = A(Q.zw).xy;
    if (length(U-Q.zw)<length(U-Q.xy))
        Q = Q.zwxy;
    if (iFrame<1) {
        Q = vec4(U,U+1.);
    }
}

// Color 🌈
Main {
    vec4 d = D(U);
    Q = C(d.xy);

    if (iFrame < 1)
      Q = (.5+.5*sin(.1*U.y+vec4(1,2,3,4)));
}

// Apply Forces 🐲
Main 
{
    Q = A(U);
    vec4 cQ = C(Q.xy); 
    vec2 f = vec2(-1e-3);
    for (float x=-3.;x<=3.;x++)
    for (float y=-3.;y<=3.;y++)
    {
        vec2 u = Q.xy+vec2(x,y);
        vec4 b = B(u),
             c = C(b.zw),
             a = A(b.zw);
        vec2 r = Q.xy-a.xy;
        float l = length(r); 
        float s = step(ln(u,b.xy,b.zw),1.);
        if (l>0.&&b.z>1.&&b.w>1.)
        {
            float q = -.5*dot(cQ,c);
            f +=    s*r/l*exp(-l/2.);
            f += .5*s*q*r/l/l;
        }
    }
    Q.zw +=   f;
    Q.xy +=  f + Q.zw;
    if (length(Q.zw)>1.)Q.zw=normalize(Q.zw);
    if (iMouse.z>0.&&length(U-iMouse.xy)<5.) {
        Q = vec4(U,0,0);
    }
    if (Q.x<1.){Q.x=1.;Q.z*=-1.;}
    if (Q.y<1.){Q.y=1.;Q.w*=-1.;}
    if (R.x-Q.x<1.){Q.x=R.x-1.;Q.z*=-1.;}
    if (R.y-Q.y<1.){Q.y=R.y-1.;Q.w*=-1.;}
    if (iFrame<1) {
        Q = vec4(round(U/9.)*9.,0,0);
    }
}
