vec2 R;
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {return texture(iChannel1,U/R);}
vec4 C (vec2 U) {return texture(iChannel2,U/R);}
void mainImage( out vec4 Q, in vec2 U)
{
    R = iResolution.xy;
    vec2 M = iMouse.z>0.?iMouse.xy:0.5*R;
    vec2 r = 2.*(U-M)/R.y;
    r = r/sqrt(length(r));
    Q = vec4(0);
    for (float i = 1.; i < 10.; i++) {
        vec4 c = C(U-i*r);
        Q += c*c*exp(-.2*i);
    }
    Q = mix(C(U),.8*Q*exp(-1.5*length(r)),.5);
}

// Fluid
vec2 R;
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {return texture(iChannel1,U/R);}
vec4 T (vec2 U) {return A(B(U).xy);}
void mainImage( out vec4 C, in vec2 U )
{
   R = iResolution.xy;
   C = T(U);
   vec4 
        n = T(U+vec2(0,1)),
        e = T(U+vec2(1,0)),
        s = T(U-vec2(0,1)),
        w = T(U-vec2(1,0));
   C.x -= 0.25*(e.z-w.z+(n.w*C.w-s.w*C.w));
   C.y -= 0.25*(n.z-s.z+(e.w*C.w-w.w*C.w));
   C.z  = 0.25*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z));
   C.w  = 0.25*((s.x-n.x+w.y-e.y)-(n.w+e.w+s.w+w.w));


   C.xy += exp(-length(U.xy-0.5*R))*(0.9*vec2(sin(0.2*iTime),cos(0.2*iTime))-C.xy);
   if (U.x < 1.||R.x-U.x<1.)C.xy*=0.;
   if (U.y < 1.||R.y-U.y<1.)C.xy*=0.;
   if (iFrame < 1) C = vec4(0);
    vec4 mo = texture(iChannel3,vec2(0));
   float l = ln(U,mo.xy,mo.zw);
   if (mo.z > 0. && l < 2.) C += vec4(3.*(2.-l)*(mo.xy-mo.zw)/R.y,0,0);


}

vec2 R;
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {return texture(iChannel1,U/R);}
void mainImage( out vec4 C, in vec2 U)
{
    R = iResolution.xy;
    float 
  		n = A(U+vec2(0,1)).z,
  		e = A(U+vec2(1,0)).z,
  		s = A(U-vec2(0,1)).z,
  		w = A(U-vec2(1,0)).z;
    #define N 2.
    for (float i = 0.; i < N; i++)
        U -= A(U).xy/N;
    C.xy = U;
    C.zw = vec2(e-w,n-s);
}

vec2 R;
#define D 5
float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));}
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {return texture(iChannel1,U/R);}
vec4 C (vec2 U) {return texture(iChannel2,U/R);}
float dI (vec2 U, vec3 me, vec3 light, float mu) {
	vec3 r = vec3(U,100);
   	vec3 n = normalize(vec3(B(r.xy).zw,mu));
    vec3 li = reflect((r-light),n);
    float len = ln(me,r,li);
    return 2.5*exp(-1.7*len);
}
float I (vec2 U, vec3 me, vec3 light, float mu) {
	float intensity = 0.;

    for (int x = -D; x <= D; x++)
    for (int y = -D; y <= D; y++)
	intensity += dI(U+vec2(x,y),me,light,0.1*mu);
    return intensity;
}
vec3 S (vec2 U, vec3 me, vec3 light, float mu) {
    return I (U,me,light,mu)*vec3(exp(-(mu-0.5)*(mu-0.5)),exp(-(mu-1.0)*(mu-1.0)),exp(-(mu-1.4)*(mu-1.4)));
}
void mainImage( out vec4 Q, in vec2 U)
{
   	R = iResolution.xy;
    vec3 light = vec3(0.5*R,1e5);
    vec3 me    = vec3(U,0);

    vec3 c = vec3(0);
    for (float mu = .4; mu <= 1.6; mu+=.4) 
        c += S(U,me,light,mu);
   	Q = vec4(0.03*c,1);
    if (R.x >= 800.) Q = mix(Q,C(U),.5);


}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

#define pi2 6.2831
#define m_c 261.625565
#define pw 1.05946309436
vec3 hash31(float p) // Dave Hoskins
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
float X (
    	 float time,
    	 float phase,
    	 float octave, 
         float note, 
         float attack,
         float sustain,
         float release,
         float nl
        ) {
    float phi = phase*pi2;
    float w = m_c*pi2*exp2(octave)*pow(pw,note);
    float envelope = smoothstep(0.,attack,time-phase)*smoothstep(release,0.,time-phase-attack-sustain);
    return (cos(w*time-phi+50.*cos(0.001*nl*w*time))+cos(0.25*w*time-phi+30.*cos(0.002*nl*w*time)))*envelope;
}

vec2 mainSound( in int samp, float t )
{
    float a = 0.;
    #define N 15.
   for (float i = 0.; i < N; i++) {
    t += 0.01;
    float time = 0.;
    float note = 0.;
    float root = 0.;
    float octave = 1.;
    vec3 h;
    float d=1.;
    time = floor(t);
    h = hash31(time);
    root = floor(h.x*12.)-12.;
    h = hash31(time+i);
    if (h.x>0.4) {
        d *= 0.5;
    	time = floor(t);
        h = hash31(2.*time+i*100.);
        if (h.x>0.6) {
            d *= 0.5;
            time = floor(t*2.)/2.;
            h = hash31(20.*time+i*120.);
        }
    }
    note = 2.*floor(h.z*6.)-4.;



    a += 0.5*X (t,time,octave,root+note,0.01*d,0.9*d,0.1*d,0.2*h.x);

   }

    return vec2(a)/N;
}
