void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
    fragColor = a*4.+.5;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 a = A(u+vec2(0,0));
    float b = 0.;

    //kernel convolution that pointifies
    {
        float z = 2.;//kernel convolution size
        float t = z*2.+1.;
              t = 1./(t*t-1.);
        float blur = 1.5/z;
        for(float i = -z; i<=z;++i){
        for(float j = -z; j<=z;++j){
          float s0 = 0.;
          float s1 = 0.;
          for(float i2 = -z; i2<=z;++i2){
          for(float j2 = -z; j2<=z;++j2){
          vec2 c = vec2(i2,j2)*blur;
          s0 += max(0.,+A(u+vec2(i,j)+vec2(i2,j2)).x)/exp(dot(c,c));
          s1 += max(0.,-A(u+vec2(i,j)+vec2(i2,j2)).x)/exp(dot(c,c));
          }}
          if(s0==0.)s0 = 1.;
          if(s1==0.)s1 = 1.;
          vec2 c = -vec2(i,j)*blur;
          float d = A(u+vec2(i,j)).x;
          float e = 1./exp(dot(c,c))*d*(1.-min(abs(d)+.01,1.));
          b +=(+max(0.,+a.x)/s0
               +max(0.,-a.x)/s1)*e;
        }}
    }
    //kernel convolution that blurs
    {
        float z = 2.;//kernel convolution size
        float t = z*2.+1.;
              t = 1./(t*t-1.);
        float blur = 1.5/z;
        for(float i = -z; i<=z;++i){
        for(float j = -z; j<=z;++j){
          float s = 0.;
          for(float i2 = -z; i2<=z;++i2){
          for(float j2 = -z; j2<=z;++j2){
          vec2 c = vec2(i2,j2)*blur;
          s += 1./exp(dot(c,c));
          }}
          if(s==0.)s = 1.;
          vec2 c = -vec2(i,j)*blur;
          float d = A(u+vec2(i,j)).x;
          b += d/s/exp(dot(c,c))*(min(abs(d)+.0,1.))*1.;
        }}
    }
    a = vec4(b,a.xyz);

    if(iMouse.z>0.)
    {
        vec2 m1 = 2.*(u-iMouse.xy)/iResolution.y;
        a *= 1.-1./exp(dot(m1,m1));
    }
    if(iFrame==0)
    {
        vec2 m1 = (2.*u-iResolution.xy)/iResolution.y-vec2(-.02,0);
        vec2 m2 = (2.*u-iResolution.xy)/iResolution.y-vec2(+.02,0);
        a += +.1/exp(dot(m1,m1)*333.)
             -.1/exp(dot(m2,m2)*333.);
    }
    float keyA  = texture( iChannel1, vec2(65.5/256.,.25) ).x;
    if(keyA!=0.)a = texture( iChannel2, u/iResolution.xy).xxxx-.6;
    fragColor = a;
}
