// Fork of "Water Fall" by wyatt. https://shadertoy.com/view/NtKGWD
// 2021-12-30 04:31:49

void mainImage(out vec4 Q, in vec2 U)
{
    vec4 f = A(U),b=B(U);
    vec4 m = vec4(0);
    box if(x!=0&&y!=0) {m+=1./4.*A(U+vec2(x,y));}
    Q = max(1.-30.*(f-m).wwww,0.);
    Q -= f.w*(1.-sin((1.+.5*f.z)*vec4(.5,.7,1,1)));
    Q -= .3*b.wwww;
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
//#define Main void mainImage(out vec4 Q, in vec2 U)
#define box for(int x=-1;x<=1;x++)for(int y=-1;y<=1;y++)
#define r2 0.70710678118
// oneshade:
//https://www.shadertoy.com/view/7sKSRh
float std;
float erf(in float x) {
    x *= std;
    //return sign(x) * sqrt(1.0 - exp(-1.239192 * x * x));
    return sign(x) * sqrt(1.0 - exp2(-1.787776 * x * x)); // likely faster version by @spalmer
}
float erfstep (float a, float b, float x) {
    return .5*(erf(b-x)-erf(a-x));
}

// Forces
void mainImage(out vec4 Q, in vec2 U)
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u),b=B(U+u);
        float f = 0.05*(a.w*(a.w-.8)+b.w);
        dQ.xy -= f*u;
    }
    Q += dQ;
    Q.y -= .2/R.y;
    Q = clamp(Q,-2.,2.);
    vec2 M = 1.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    if(length(U-M)<.02*R.y)Q = vec4(0,0,0.*sin(iTime),1.);
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0);
    if (U.x<3.&&U.y>.9*R.y)Q.w=1.;

    if (R.x-U.x<3.&&U.y>.9*R.y)Q.w=1.;
    if (U.x<1.||U.y<1.||R.y-U.y<1.) Q.xy*=0.;
    if(R.x-U.x<1.)Q.xy *= 0.;
}

// Advect
void mainImage(out vec4 Q, in vec2 U)
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       {
           float j = 1.;
           float k = 1.;
           float wa = 0.25*Q.w*min(i,j)/j;
           float wb = 0.25*q.w*max(k+i-1.,0.)/k;
            dQ.w += wa+wb;
      }
      {
           float j = 1.;
           float k = 1.;
           float wa = 0.25*Q.w*min(i,j)/j;
           float wb = 0.25*q.w*max(k+i-1.,0.)/k;
            dQ.xyz += Q.xyz*wa+q.xyz*wb;
      }

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}

// Forces
void mainImage(out vec4 Q, in vec2 U)
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u),b=B(U+u);
        float f = 0.1*(a.w+b.w);
        dQ.xy -= f*u;
    }
    Q += dQ;
    Q = clamp(Q,-2.,2.);
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0,0,0,.1);
    if (U.x<1.||U.y<1.||R.y-U.y<1.) Q.xy*=0.;
    if(R.x-U.x<1.)Q.xy *= 0.;
}

// Advect
void mainImage(out vec4 Q, in vec2 U)
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .55;
       float k = .55;
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}
