vec2 R;
vec4 M;
float T;
int I;
#define A(U) texture(cha,(U)/R)
#define B(U) texture(chb,(U)/R)
#define Main void mainImage (out vec4 Q, in vec2 U)
float signe (float x) {return atan(100.*x);}
void prog (vec2 U, out vec4 a, out vec4 b, sampler2D cha, sampler2D chb) {

    a = vec4(0); b = vec4(0);
    float n = 0.;
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    {
        vec2 u = vec2(x,y);
    	vec4 aa = A(U+u), bb = B(U+u);
        #define q 1.075
		vec4 w = clamp(vec4(aa.xy-0.5*q,aa.xy+0.5*q),U.xyxy - 0.5,U.xyxy + 0.5);
        float m = (w.w-w.y)*(w.z-w.x)/(q*q);
        aa.xy = 0.5*(w.xy+w.zw);
        a += aa*bb.x*m;
        b.x += bb.x*m;
        b.yzw += bb.yzw*bb.x*m;
        n += bb.x;
    }
    if (b.x>0.) {
        a/=b.x;
        b.yzw/=b.x;
        b.yzw = B(a.xy-a.zw).yzw;
        a.zw = mix(A(a.xy-a.zw).zw,a.zw,clamp(2.*n,0.,1.));
    }
}
void prog2 (vec2 U, out vec4 a, out vec4 b, sampler2D cha, sampler2D chb) {

    a = A(U); b = B(U);
    vec2 f = vec2(0); float m = 0., p = 0., z = 0.;
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    {
        vec2 u = vec2(x,y);
    	vec4 aa = A(U+u), bb = B(U+u);
        float l = length(u);
        if (l>0.) {
            f += 0.125*b.x*bb.x*(.1+bb.x*bb.y)*u/l;
            f += 0.125*b.x*bb.x*(b.z*b.w)*vec2(-u.y,u.x)/l;
            p += 0.125*b.x*bb.x*dot(u/l,aa.zw-a.zw);
            z += 0.125*b.x*bb.x*dot(vec2(-u.y,u.x)/l,aa.zw-a.zw);
            m += bb.x;
        }
    }
    if (m>0.) {
       a.zw += f/m;
       a.xy += f/m;
       b.y += p/m;
       b.z += z/m;
    }
    a.xy += a.zw;


    // Boundaries:
   	a.w -= .01/R.y*signe(b.x);
    if (a.x<10.) {a.z -= -.1;b.y*=0.9;}if (R.x-a.x<10.) {a.z -= .1;b.y*=0.9;}if (a.y<10.) {a.w -= -.1;b.y*=0.9;}if (R.y-a.y<10.) {a.w -= .1;b.y*=0.9;}
    if (I<1||U.x<1.||R.x-U.x<1.||R.y-U.y<1.||R.x-U.x<1.) {
    	a = vec4(U,0,0);
        b = vec4(0);
        if (length(U-0.5*R) < 0.3*R.y&&length(U-0.5*R)>0.) {b.x = 2.;a.zw = -.2*normalize(U-0.5*R);}
    	b.w = 0.;
    }
    if (M.z>0.) {
        float l = length(U-M.xy);
        float s = smoothstep(1.,8.,l);
        b.x = mix(2.,b.x,s);
        a.xy = mix(U,a.xy,s);
        a.zw = mix(.25*vec2(cos(.4*T),sin(.4*T)),a.zw,s);
        if (l<8.) b.w = 1.+sin(.1*T);
    }
}
