// Fork of "Line Tracking Fluid" by wyatt. https://shadertoy.com/view/tsKXzd
// 2020-12-10 19:40:02

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 d = D(U);
    float l = ln(U,a.xy,a.zw);
    Q = max(cos(d.z*vec4(1,2,3,4)),.1)*(exp(-l));
}

void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
}
Main {
    Q = A(U);
    for (int x = -2;x <=2; x++)
    for (int y = -2;y <=2; y++)
    X(Q,U,vec2(x,y));
    Q.xy = mix(Q.xy,A(Q.xy).xy,1.);
    Q.zw = mix(Q.zw,A(Q.zw).zw,.01);
    Q.xy += D(Q.xy).xy;
    Q.zw += D(Q.zw).xy;

    if (length(Q.xy-Q.zw) > 2.5) {
        vec2 m = 0.5*(Q.xy+Q.zw);
        if (length(U-Q.xy) > length(U-Q.zw)) 
        	Q.xy = m;
        else Q.zw = m;
    }
    if (iFrame<1) {
        Q = vec4(0.51*R,0.49*R);
        vec4 a =vec4(vec2(0.49,.51)*R,vec2(.51,.49)*R);
        if (ln(U,a.xy,a.zw)<ln(U,Q.xy,Q.zw))
            Q = a;
    }

}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

vec4 T(vec2 U) {
	U -= .5*D(U).xy;
	U -= .5*D(U).xy;
    return D(U);
}
Main {
    Q = T(U);
    vec4 
        n = T(U+o.yx),
        e = T(U+o.xy),
        s = T(U+o.yz),
        w = T(U+o.zy),
        m = 0.25*(n+e+s+w);
    Q.xy = m.xy-0.25*vec2(e.z-w.z,n.z-s.z);
	Q.z = Q.z-0.25*(n.y+e.x-s.y-w.x);
    vec4 a = A(U);
    float l = ln(U,a.xy,a.zw);
    float v = exp(-l);
    Q.z -= .001*v;
    Q.xy = mix(Q.xy,-norm(a.xy-a.zw),.1*v*exp(-Q.z*Q.z));
    Q.xy *= .5;
    if (iMouse.z>0.) Q.xy += exp(-.1*length(U-iMouse.xy))*(U-iMouse.xy);
    if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.) Q.z *= 0.;
	if (iFrame < 1) Q = vec4(0,0,0,0);
}
