vec2 R;
float T;

#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float ln (vec3 p, vec3 a, vec3 b) {
    float l = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
    return mix(.75,1.,l)*length(p-a-(b-a)*l);
}
vec4 map (vec3 u) {
    u.xz *= ei(1.1+T);
    u.xy *= ei(1.1);
    float d = 1e9;
    vec4 c=vec4(0);
    float sg = 1e9;
    float l = .08;
    u.y = abs(u.y);
    u.y+=.1;
    mat2 M1 = ei(1.);
    float w = 0.02;
    mat2 M2 = ei(.6);
    mat2 M3 = ei(.4+.2*sin(T));
    for (float i = 1.; i < 20.; i++)
    {
        sg = ln(u,vec3(0),vec3(0,l,0))/l;
        d = min(d,sg*l-w);
        w *= .7;
        u.y -= l;
        u.xz *= M1;
        u.xz = abs(u.xz);
        u.zy *= M3;
        l *= .75;
        c += exp(-sg*sg)*(.7+.5*sin(2.+3.*i/16.+vec4(1,2,3,4)));
    }
    return vec4(c.xyz,d);
}


void mainImage(out vec4 Q, vec2 U ) {
    R = iResolution.xy; T = iTime;
    Q = vec4(0);
    vec3 p = vec3( .3*(U-.5*R)/R.y, 2);
    vec3 d = normalize( vec3(0,0,.74) - p );
    for (float i = 0.; i < 30.; i++){
        vec4 x = map(p);
        float f = 1./(1.+1e3*abs(p.z-.2));
        p += d* max( abs(x.w), 2e-5/f );
        Q += .01*x*exp(-1e3*(x.w*x.w)*f);

    }
}
